  let selectedCategory = $state<string | null>(null);

  let filteredCategories = $derived(() => {
    if (!searchQuery) {
      if (selectedCategory) {
        return emojiCategories.filter((c) => c.name === selectedCategory);
      }
      return emojiCategories;
    }
    
    const query = searchQuery.toLowerCase();
    return emojiCategories
      .map((category) => ({
        ...category,
        emojis: category.emojis.filter((item) => 
          item.emoji.includes(query) || item.desc.toLowerCase().includes(query)
        ),
      }))
      .filter((category) => category.emojis.length > 0);
  });

  function copyEmoji(emoji: string) {
    navigator.clipboard.writeText(emoji);
    copiedEmoji = emoji;
    setTimeout(() => {
      copiedEmoji = null;
    }, 300);
  }

  function getTotalCount() {
    return emojiCategories.reduce((sum, cat) => sum + cat.emojis.length, 0);
  }
